mapper.select:
SELECT * FROM tb_product
ORDER BY prod_id DESC
LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO tb_product (
prod_name, prod_price, prod_stock, prod_category, prod_desc, prod_img, prod_status, reg_date
) VALUES (
#{prodName}, #{prodPrice}, #{prodStock}, #{prodCategory}, #{prodDesc}, #{prodImg}, 'ON_SALE', NOW()
)

mapper.update:
UPDATE tb_product
SET prod_stock = prod_stock - #{quantity}
WHERE prod_id = #{prodId}
AND prod_stock >= #{quantity}
