mapper.update:
UPDATE tb_user
SET user_name = #{userName},
email = #{email},
role = #{role},
status = #{status}
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO tb_user (
user_id, user_name, password, email, role, status, created_at
) VALUES (
#{userId}, #{userName}, #{password}, #{email}, #{role}, #{status}, NOW()
)

mapper.update:
UPDATE tb_user
SET last_login = NOW()
WHERE user_id = #{userId}
